import React, {Component, Fragment} from 'react';
import {
    Content,
    ContentLeft,
    ContentRight,
    ContentTitle,
    ContentContent
} from './style';
import { connect } from 'react-redux';
class LeftContent extends Component{
    render () {
        return (
            <Content>
                {
                    this.props.list.map((item) => (
                       <Fragment>
                           <ContentLeft>
                               <ContentTitle>{item.get('title')}</ContentTitle>
                               <ContentContent>{item.get('desc')}</ContentContent>
                           </ContentLeft>
                           <ContentRight>
                               <img className='img' src={item.get('imgUrl')}/>
                           </ContentRight>
                       </Fragment>
                    ))
                }
                <ContentLeft>
                    <ContentTitle>胡歌12年后首谈车祸：既然活下来了，就不能白白活着</ContentTitle>
                    <ContentContent>文/麦大人 01 胡歌又刷屏了。 近日他上了《朗读者》，而这一期的主题是“生命”，他用磁性的嗓音，朗读了一段《哈姆雷特》中的经典独白，相当震撼：...</ContentContent>
                </ContentLeft>
                <ContentRight>
                    <img className='img' src='//upload-images.jianshu.io/upload_images/2259045-2986b9be86b01f63?imageMogr2/auto-orient/strip|imageView2/1/w/300/h/240'/>
                </ContentRight>
            </Content>
        )
    }
}
const mapState = (state) => ({
    list:state.get('home').get('articleList')
})
export default connect(mapState,null)(LeftContent);
