import React, {Component} from 'react';
import { connect } from 'react-redux';
import '../../static/iconfont/iconfont.css';
import { CSSTransition } from 'react-transition-group';
import { actionCreators }  from '../header/store'
import {
    HeaderWrapper,
    Logo,
    Nav,
    NavItem,
    SearchInput,
    SearchInfo,
    SearchInfoTitle,
    SearchInfoSwitch,
    SearchInfoList,
    SearchInfoItem,
    Button,
    Addition,
    Btn
} from "./style";
class Header extends Component{
    render () {
        return(
            <HeaderWrapper>
                <Logo/>
                <Nav>
                    <NavItem className='left active'>首页</NavItem>
                    <NavItem className='left'>下载App</NavItem>
                    <NavItem className='right'>登录</NavItem>
                    <NavItem className='right'>
                        <i className='iconfont'>&#xe636;</i>
                    </NavItem>
                    <SearchInput>
                        <CSSTransition
                            in={this.props.focus}
                            timeout={200}
                            classNames='slide'
                        >
                            <Button
                                className={this.props.focus ? 'focus': ''}
                                onFocus={this.props.handleInputFocus}
                                onBlur={this.props.handleInputBlur}
                            ></Button>
                        </CSSTransition>
                        <i className={this.props.focus ? 'focus input-search iconfont' : 'input-search iconfont'}>&#xe609;</i>
                    </SearchInput>
                    {this.getList()}
                </Nav>
                <Addition>
                    <Btn className='reg'>注册</Btn>
                    <Btn className='write'>
                        <i className='iconfont'>&#xe615;</i>
                        写文章
                    </Btn>
                </Addition>
            </HeaderWrapper>
        )
    }
    getList () {
        const JSList = this.props.list.toJS()
        const pageList = []
        for (let i = (this.props.page-1)*10;i < this.props.page*10;i ++){
            pageList.push(<SearchInfoItem key={JSList[i]}>{JSList[i]}</SearchInfoItem>)
        }
        if (this.props.focus) {
            return (
                <SearchInfo
                    onMouseEnter={this.handleMouseEnter}
                    onMouseLeave={this.handleMouseLeave}
                >
                    <SearchInfoTitle>热门搜索</SearchInfoTitle>
                    <SearchInfoSwitch>换一批</SearchInfoSwitch>
                    <SearchInfoList>
                        {pageList}
                    </SearchInfoList>
                </SearchInfo>
            )
        } else{
            return null;
        }
    }
}
const mapStateToProps = (state) => {
    return {
        focus:state.getIn(['header','focus']),
        list:state.getIn(['header','list']),
        page:state.getIn(['header','page']),
        mouseIn:state.getIn(['header','mouseIn'])
    }
}
const MapDispatchToProps = (dispatch) => {
    return {
        handleInputFocus () {
            dispatch(actionCreators.getList())
            dispatch(actionCreators.searchFocus())
        },
        handleInputBlur () {
            dispatch(actionCreators.searchBlur())
        },
        handleMouseEnter () {
            dispatch(actionCreators.mouseEnter())
        }
    }

}
export default connect(mapStateToProps,MapDispatchToProps)(Header);
