import React,{ Component } from 'react';
import {
    HomeWrap,
    HomeLeft,
    HomeRight,
    LeftBr,
    LoadMore
} from './style';
import {connect} from 'react-redux'
import LeftBanner from './component/LeftBanner';
import LeftContent from './component/LeftContent';
import LeftRecommend from './component/LeftRecommend';
import { actionCreators } from './store';
import RightHeader from './component/RightHeader'
class Home extends Component {
    render () {
        return(
            <HomeWrap>
                <HomeLeft>
                    <LeftBanner/>
                    <LeftRecommend/>
                    <LeftBr/>
                    <LeftContent/>
                    <LoadMore onClick={() => (this.props.LoadMoreData())}>更多内容</LoadMore>
                </HomeLeft>
                <HomeRight><RightHeader/></HomeRight>
            </HomeWrap>
        )
    }
    componentDidMount () {
        this.props.getHomeData()
    }
}
const MapDispatch = (dispatch) => ({
    getHomeData() {
        dispatch(actionCreators.getList())
    },
    LoadMoreData (){
        dispatch(actionCreators.LoadMoreData())
    }
 })
export default connect(null,MapDispatch)( Home);
