import React,{Component} from 'react';
import {connect} from 'react-redux';
import '../../store';
import {actionCreators } from '../../store'
import {
    RecommendWrap,
    RecommendItem
} from './style';
class LeftRecommend extends Component{
    render () {
        const {list} = this.props
        return (
          <RecommendWrap>
              {
                  list.map((item)=> (
                      <RecommendItem>
                          <img
                              className='img'
                              src='//upload.jianshu.io/collections/images/261938/man-hands-reading-boy-large.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/64/h/64'
                          />
                          {item.get('title')}
                      </RecommendItem>
                  ))
              }
          </RecommendWrap>
        )
    }
}
const mapState = (state) => ({
    list:state.get('home').get('recommendList')
})
const mapDispatchToProps = (dispatch) => ({
  return () {
      dispatch(actionCreators.ChangeList())
  }
})
export default connect(mapState,mapDispatchToProps)(LeftRecommend);
